INSERT INTO `language_text` VALUES ('en', '_check','_RATIONALE_10','People with photosensitive epilepsy can have seizures triggered by flickering or flashing in the 4 to 59 flashes per second (Hertz) range with a peak sensitivity at 20 flashes per second as well as quick changes from dark to light (like strobe lights).','2008-11-24 13:02:09','');
