INSERT INTO `language_text` VALUES ('en', '_guideline','_SUBGROUPNAME_32','10.2 Until user agents support explicit associations between labels and form controls, for all form controls with implicitly associated labels, ensure that the label is properly positioned.','2008-11-24 13:03:29','');
